export function checkCredentials(params) {
  if (params.username.length < 5) {
    params.errorMsg = 'Логин должен быть длиннее 5 символов'
    return false
  }

  if (
    params.username.toLowerCase() !== 'admin' ||
    params.password !== '12345'
  ) return false

  return true
}
