// export const LOG_OUT = 'LOG_OUT'
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE'

export function logIn(params, cb) {
    return 
    dispatch({
        type: LOG_IN,
      })

    tttt()
    .then(res => {
        if (checkCredentials(params)) {
            dispatch({
                // type: LOG_IN,
