                data: res
            })
            cb()

        } else {
            dispatch({
                type: LOG_IN_FAILURE,
                payload: {
                    errorMsg: params.errorMsg,
                },
                error: true,
            })
        }
    })
    .catch(error => {
        dispatch({
          type: PROFILE_GET_FAILURE,
          payload: {
            errorMsg: 'Сервер временно недоступен',
          },
          error: true,
        })
      })
}

