import React from 'react'
import './css/list.css'

class List extends React.Component {

    handleClick = (key) => {
        this.props.display([...this.props.data], key)
    };

    render() {
        const arr = this.props.data.map((element, key) => (
            <div key={element.id} className="element">
                <div className="elementHeader">
                    <div className="elementTitleIdBlock">
                        <div className="elementTitle">
                            {element.title}
                        </div>
                        <div className="elementId">
                            {element.id}
                        </div>
                    </div>
                    <div className="elementShowButton">
                        <button onClick={() => this.handleClick(key)}>&#5169;</button>
                    </div>
                </div>
                <div className={`elementBody ${this.props.data[key].isClose ? 'elementBodyClose' : ''}`}>
                    {element.body}
                </div>
            </div>
        ))
        return (
            <div className="box">
                <input
                    placeholder={'Поиск по теме'}
                />
                <div className="filter">
                    <div>Идентификатор</div>
                    <div>&#8593;</div>
                    <div>Заголовок</div>
                    <div>&#8595;</div>
                    <button>Сбросить фильтры</button>
                </div>
                <div className="elementBox">
                    {arr}
                </div>
            </div>
        )
    }
}

export default List
