import { checkCredentials } from '../utils/session'
import { data } from '../utils/network'

export const LOG_IN = 'LOG_IN'
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE'
export const DISPLAY_DATA = 'DISPLAY_DATA'

export function logIn(params, cb) {
    return dispatch => {
        data()
            .then(res => {
                if (checkCredentials(params)) {
                    dispatch({
                        type: LOG_IN,
                        payload: {
                            name: params.username,

                        },
                        data: res,

                    })
                    disp: () => { console.log('privet') }
                    cb()
                } else {
                    dispatch({
                        type: LOG_IN_FAILURE,
                        payload: {
                            errorMsg: params.errorMsg,
                        },
                        error: true,
                    })
                }
            })
            .catch(error => {
                dispatch({
                    type: LOG_IN,
                    payload: {
                        errorMsg: 'Сервер временно недоступен',
                    },
                    error: true,
                })
            })
    }
}

export function display(data, key) {
    data[key].isClose = data[key].isClose ? false : true
    return dispatch => {
        dispatch({
            type: DISPLAY_DATA,
            data: data,
        })
    }
}

export function sortById(data) {
    function compareNumeric(a, b) {
        if (a > b) return 1;
        if (a == b) return 0;
        if (a < b) return -1;
    }
    data.id.sort(compareNumeric);
    return dispatch => {
        dispatch({
            type: DISPLAY_DATA,
            data: data,
        })
    }
}
