import React from 'react'
import './css/list.css'

class List extends React.Component {

    handleClick = (key) => {
        this.props.display([...this.props.data, this.props.data[key].isActive = true])
        console.log(this.props.data)
    };

    render() {
        // const isActive = this.state.isActive;
        // this.props.data.map((element) => (this.setState({ element: !this.state.isActive })))
        const arr = this.props.data.map((element, key) => (
            <div key={element.id} className="element">
                <div className="elementHeader">
                    <div>
                        {element.title}
                    </div>
                    <div className="elementId">
                        {element.id}
                    </div>
                    <div>
                        <button onClick={() => this.handleClick(key)}>^</button>
                    </div>
                </div>
                <div className={`${this.props.data[key].isActive ? 'elementBody' : ''}`}>
                    {element.body}
                </div>
            </div>
        ))
        return (
            <div className="box">
                <input
                    placeholder={'Поиск по теме'}
                />
                <div className="filter">
                    <div>Идентификатор</div>
                    <div>Заголовок</div>
                    <button>Сбросить фильтры</button>
                </div>
                <div className="elementBox">
                    {arr}
                </div>
            </div>
        )
    }
}

export default List
