const API_ROOT = 'https://jsonplaceholder.typicode.com/todos'

let arr;

const tttt = async () => {
  try {
    const response = await fetch(API_ROOT)
    if (response.ok) {
      arr = await response.json()
      console.log(arr)
      dispatch({ type: 'todos/todosLoaded', data: arr })
    } else {
      throw new Error(response.status)
    }
  } catch (err) {
    console.warn('httpGet error ', err)
  }
}

export {arr, tttt}

